import React, { useState } from "react";
import { Box, Typography, TextField, Button, Alert } from "@mui/material";
import { useNavigate } from "react-router-dom";

export default function Login() {
  const [form, setForm] = useState({ email: "", password: "" });
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    
    console.log("Login attempt with:", form);
    
    try {
      const res = await fetch("http://localhost:5000/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
        credentials: "include"
      });
      const data = await res.json();
      console.log("Login response:", data);
      
      if (res.ok && data.role) {
        sessionStorage.setItem("userName", data.name || ""); // Save user name
        // Redirect based on role
        if (data.role === "victim") navigate("/victim_dashboard");
        else if (data.role === "officer") navigate("/officer_dashboard");
        else if (data.role === "admin") navigate("/admin_dashboard");
        else navigate("/");
      } else {
        setError(data.error || "Login failed");
      }
    } catch (error) {
      console.error("Login error:", error);
      setError("Network error");
    }
  };

  return (
    <Box maxWidth={400} mx="auto" mt={8}>
      <Typography variant="h5" mb={2}>Login</Typography>
      {error && <Alert severity="error">{error}</Alert>}
      <form onSubmit={handleSubmit}>
        <TextField 
          label="Email" 
          name="email" 
          value={form.email}
          fullWidth 
          margin="normal" 
          onChange={handleChange} 
          required 
        />
        <TextField 
          label="Password" 
          name="password" 
          type="password" 
          value={form.password}
          fullWidth 
          margin="normal" 
          onChange={handleChange} 
          required 
        />
        <Button type="submit" variant="contained" fullWidth sx={{ mt: 2 }}>Login</Button>
        <Button onClick={() => navigate("/register")} fullWidth sx={{ mt: 1 }}>Sign Up</Button>
      </form>
    </Box>
  );
}
