import React, { useState, useRef } from "react";
import {
  Box, Typography, TextField, Button, MenuItem, Alert, Grid, Paper, InputAdornment, IconButton, Container
} from "@mui/material";
import DescriptionIcon from '@mui/icons-material/Description';
import EventIcon from '@mui/icons-material/Event';
import PlaceIcon from '@mui/icons-material/Place';
import NotesIcon from '@mui/icons-material/Notes';
import UploadFileIcon from '@mui/icons-material/UploadFile';
import InfoOutlinedIcon from '@mui/icons-material/InfoOutlined';
import DeleteIcon from '@mui/icons-material/Delete';
import { useNavigate } from "react-router-dom";

const crimeTypes = [
  "Hacking", "Scam", "Cyberbullying", "Phishing", "Other"
];

export default function ReportCrime() {
  const [form, setForm] = useState({
    crime_type: "",
    date: "",
    location: "",
    description: "",
    files: [],
  });
  const [filePreviews, setFilePreviews] = useState([]);
  const [success, setSuccess] = useState("");
  const [error, setError] = useState("");
  const [submitting, setSubmitting] = useState(false);
  const fileInputRef = useRef();
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === "files") {
      const fileArr = Array.from(files);
      setForm({ ...form, files: fileArr });
      const previews = fileArr.map(file => ({
        name: file.name,
        type: file.type,
        url: file.type.startsWith("image/") ? URL.createObjectURL(file) : null
      }));
      setFilePreviews(previews);
    } else {
      setForm({ ...form, [name]: value });
    }
  };

  const handleRemoveFile = (index) => {
    const newFiles = form.files.filter((_, i) => i !== index);
    setForm({ ...form, files: newFiles });
    setFilePreviews(filePreviews.filter((_, i) => i !== index));
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const fileArr = Array.from(e.dataTransfer.files);
    setForm({ ...form, files: fileArr });
    const previews = fileArr.map(file => ({
      name: file.name,
      type: file.type,
      url: file.type.startsWith("image/") ? URL.createObjectURL(file) : null
    }));
    setFilePreviews(previews);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(""); setSuccess("");
    if (!form.crime_type || !form.date || !form.location || !form.description) {
      setError("Please fill in all required fields.");
      return;
    }
    setSubmitting(true);
    const formData = new FormData();
    formData.append("crime_type", form.crime_type);
    formData.append("date", form.date);
    formData.append("location", form.location);
    formData.append("description", form.description);
    form.files.forEach(file => formData.append("files", file));
    try {
      const res = await fetch("http://localhost:5000/victim/report", {
        method: "POST",
        body: formData,
        credentials: "include"
      });
      const data = await res.json();
      if (res.ok) {
        setSuccess("Report submitted! Your report ID is " + data.report_id);
        setForm({ crime_type: "", date: "", location: "", description: "", files: [] });
        setFilePreviews([]);
      } else {
        setError(data.error || "Failed to submit");
      }
    } catch {
      setError("Network error");
    }
    setSubmitting(false);
  };

  return (
    <Box sx={{ 
      display: 'flex', 
      minHeight: '100vh', 
      background: '#f8fafc' 
    }}>
      <Box sx={{ 
        flexGrow: 1, 
        ml: '280px', // Account for sidebar width
        width: 'calc(100% - 280px)', // Ensure proper width calculation
        minHeight: '100vh',
        py: 6 
      }}>
        <Container maxWidth="md">
          <Typography variant="h4" fontWeight={700} sx={{ mb: 1, textAlign: 'center' }}>
            Report a Cyber Crime
          </Typography>
          <Typography color="text.secondary" sx={{ mb: 4, textAlign: 'center' }}>
            Provide detailed information about the cybercrime incident
          </Typography>
          
          <Paper sx={{ p: { xs: 3, md: 5 }, borderRadius: 3, boxShadow: 3 }}>
            <form onSubmit={handleSubmit} encType="multipart/form-data">
              <Grid container spacing={3}>
                {/* Crime Type */}
                <Grid item xs={12}>
                  <Typography fontWeight={600} mb={1} display="flex" alignItems="center">
                    <DescriptionIcon sx={{ mr: 1, color: 'primary.main' }} /> 
                    Crime Type *
                  </Typography>
                  <TextField
                    select
                    name="crime_type"
                    value={form.crime_type}
                    onChange={handleChange}
                    fullWidth
                    required
                    placeholder="Select crime type"
                    variant="outlined"
                    size="large"
                  >
                    <MenuItem value="" disabled>Select crime type</MenuItem>
                    {crimeTypes.map((type) => <MenuItem key={type} value={type}>{type}</MenuItem>)}
                  </TextField>
                </Grid>

                {/* Date and Location */}
                <Grid item xs={12} sm={6}>
                  <Typography fontWeight={600} mb={1} display="flex" alignItems="center">
                    <EventIcon sx={{ mr: 1, color: 'primary.main' }} /> 
                    Date of Occurrence *
                  </Typography>
                  <TextField
                    name="date"
                    type="date"
                    value={form.date}
                    onChange={handleChange}
                    fullWidth
                    required
                    InputLabelProps={{ shrink: true }}
                    placeholder="mm/dd/yyyy"
                    variant="outlined"
                    size="large"
                  />
                </Grid>

                <Grid item xs={12} sm={6}>
                  <Typography fontWeight={600} mb={1} display="flex" alignItems="center">
                    <PlaceIcon sx={{ mr: 1, color: 'primary.main' }} /> 
                    Location *
                  </Typography>
                  <TextField
                    name="location"
                    value={form.location}
                    onChange={handleChange}
                    fullWidth
                    required
                    placeholder="e.g., Online - Email, Social Media Platform, Website URL"
                    variant="outlined"
                    size="large"
                  />
                </Grid>

                {/* Description */}
                <Grid item xs={12}>
                  <Typography fontWeight={600} mb={1} display="flex" alignItems="center">
                    <NotesIcon sx={{ mr: 1, color: 'primary.main' }} /> 
                    Description *
                  </Typography>
                  <TextField
                    name="description"
                    value={form.description}
                    onChange={handleChange}
                    fullWidth
                    required
                    multiline
                    minRows={5}
                    placeholder="Provide a detailed description of what happened, including timeline, any suspicious activities, and any other relevant information..."
                    variant="outlined"
                    size="large"
                  />
                </Grid>

                {/* Upload Evidence */}
                <Grid item xs={12}>
                  <Typography fontWeight={600} mb={1} display="flex" alignItems="center">
                    <UploadFileIcon sx={{ mr: 1, color: 'primary.main' }} /> 
                    Upload Evidence (Optional)
                  </Typography>
                  <Box
                    sx={{
                      border: '2px dashed #cbd5e1',
                      borderRadius: 3,
                      p: 4,
                      textAlign: 'center',
                      color: '#64748b',
                      cursor: 'pointer',
                      background: '#f8fafc',
                      transition: 'all 0.2s',
                      '&:hover': { 
                        borderColor: '#2563eb',
                        background: '#f1f5f9'
                      }
                    }}
                    onClick={() => fileInputRef.current && fileInputRef.current.click()}
                    onDrop={handleDrop}
                    onDragOver={handleDragOver}
                  >
                    <input
                      type="file"
                      name="files"
                      accept="image/*,application/pdf,application/msword,application/vnd.openxmlformats-officedocument.wordprocessingml.document"
                      hidden
                      multiple
                      ref={fileInputRef}
                      onChange={handleChange}
                    />
                    <Box display="flex" flexDirection="column" alignItems="center">
                      <UploadFileIcon sx={{ fontSize: 48, mb: 2, color: '#64748b' }} />
                      <Typography variant="h6" sx={{ mb: 1 }}>
                        Click to upload evidence
                      </Typography>
                      <Typography fontSize={14} color="#94a3b8">
                        Supported: Images, PDFs, Documents (Max 10MB)
                      </Typography>
                    </Box>
                    {filePreviews.length > 0 && (
                      <Grid container spacing={2} mt={3} justifyContent="center">
                        {filePreviews.map((file, idx) => (
                          <Grid item key={idx}>
                            <Box sx={{ position: 'relative', display: 'inline-block' }}>
                              {file.url ? (
                                <img src={file.url} alt={file.name} width={80} height={80} style={{ objectFit: 'cover', borderRadius: 8, border: '1px solid #ccc' }} />
                              ) : (
                                <UploadFileIcon sx={{ fontSize: 48, color: '#64748b' }} />
                              )}
                              <Typography variant="caption" display="block" sx={{ mt: 1, maxWidth: 80, overflow: 'hidden', textOverflow: 'ellipsis' }}>
                                {file.name}
                              </Typography>
                              <IconButton 
                                size="small" 
                                sx={{ 
                                  position: 'absolute', 
                                  top: -8, 
                                  right: -8,
                                  background: '#ef4444',
                                  color: '#fff',
                                  '&:hover': { background: '#dc2626' }
                                }} 
                                onClick={e => { e.stopPropagation(); handleRemoveFile(idx); }}
                              >
                                <DeleteIcon fontSize="small" />
                              </IconButton>
                            </Box>
                          </Grid>
                        ))}
                      </Grid>
                    )}
                  </Box>
                </Grid>

                {/* Important Notes */}
                <Grid item xs={12}>
                  <Paper sx={{ background: '#e8f0fe', p: 3, borderRadius: 2 }} elevation={0}>
                    <Typography fontWeight={700} color="#2563eb" mb={2} display="flex" alignItems="center">
                      <InfoOutlinedIcon sx={{ mr: 1 }} />
                      Important Notes:
                    </Typography>
                    <ul style={{ margin: 0, paddingLeft: 20, color: '#2563eb', fontSize: 15, lineHeight: 1.6 }}>
                      <li>Provide as much detail as possible for better investigation</li>
                      <li>Do not share personal passwords or sensitive information</li>
                      <li>Keep original evidence safe and secure</li>
                      <li>You will receive a report ID for tracking your case</li>
                    </ul>
                  </Paper>
                </Grid>

                {/* Alerts */}
                {error && (
                  <Grid item xs={12}>
                    <Alert severity="error">{error}</Alert>
                  </Grid>
                )}
                {success && (
                  <Grid item xs={12}>
                    <Alert severity="success">{success}</Alert>
                  </Grid>
                )}

                {/* Buttons */}
                <Grid item xs={12}>
                  <Box display="flex" justifyContent="flex-end" gap={2} mt={2}>
                    <Button
                      variant="outlined"
                      color="inherit"
                      onClick={() => navigate("/victim_dashboard")}
                      sx={{ minWidth: 120, py: 1.5 }}
                    >
                      Cancel
                    </Button>
                    <Button
                      type="submit"
                      variant="contained"
                      sx={{
                        minWidth: 180,
                        py: 1.5,
                        background: 'linear-gradient(90deg, #2563eb 0%, #1e40af 100%)',
                        color: '#fff',
                        fontWeight: 700,
                        fontSize: 16,
                        boxShadow: 'none',
                        '&:hover': {
                          background: 'linear-gradient(90deg, #1e40af 0%, #1e3a8a 100%)'
                        }
                      }}
                      disabled={submitting}
                    >
                      {submitting ? 'Submitting...' : 'Submit Report'}
                    </Button>
                  </Box>
                </Grid>
              </Grid>
            </form>
          </Paper>
        </Container>
      </Box>
    </Box>
  );
}
