import React, { useState } from "react";
import { Box, Typography, TextField, Button, Alert, Container, Paper } from "@mui/material";
import { useNavigate } from "react-router-dom";

export default function Login() {
  const [form, setForm] = useState({ email: "", password: "" });
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    
    console.log("Login attempt with:", form);
    
    try {
      const res = await fetch("http://localhost:5000/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
        credentials: "include"
      });
      const data = await res.json();
      console.log("Login response:", data);
      
      if (res.ok && data.role) {
        sessionStorage.setItem("userName", data.name || ""); // Save user name
        // Redirect based on role
        if (data.role === "victim") navigate("/victim_dashboard");
        else if (data.role === "officer") navigate("/officer_dashboard");
        else if (data.role === "admin") navigate("/admin_dashboard");
        else navigate("/");
      } else {
        setError(data.error || "Login failed");
      }
    } catch (error) {
      console.error("Login error:", error);
      setError("Network error");
    }
  };

  return (
    <Container maxWidth="sm" sx={{ mt: 8, mb: 4 }}>
      <Paper elevation={3} sx={{ p: 4, borderRadius: 2 }}>
        <Typography variant="h4" component="h1" gutterBottom align="center" sx={{ mb: 3 }}>
          CyberCrime Report System
        </Typography>
        <Typography variant="h6" gutterBottom align="center" sx={{ mb: 4 }}>
          Login to Your Account
        </Typography>
        
        {error && (
          <Alert severity="error" sx={{ mb: 3 }}>
            {error}
          </Alert>
        )}
        
        <form onSubmit={handleSubmit}>
          <TextField 
            label="Email" 
            name="email" 
            value={form.email}
            fullWidth 
            margin="normal" 
            onChange={handleChange} 
            required 
            variant="outlined"
            sx={{ mb: 2 }}
          />
          <TextField 
            label="Password" 
            name="password" 
            type="password" 
            value={form.password}
            fullWidth 
            margin="normal" 
            onChange={handleChange} 
            required 
            variant="outlined"
            sx={{ mb: 3 }}
          />
          <Button 
            type="submit" 
            variant="contained" 
            fullWidth 
            size="large"
            sx={{ mb: 2, py: 1.5 }}
          >
            Login
          </Button>
          <Button 
            onClick={() => navigate("/register")} 
            fullWidth 
            variant="outlined"
            size="large"
            sx={{ py: 1.5 }}
          >
            Create New Account
          </Button>
        </form>
      </Paper>
    </Container>
  );
}
